    {
        if (this == o)
            return true;

        if (null == o)
            return false;

        if (o instanceof Password)
        {
            Password p = (Password) o;
            //noinspection StringEquality
            return p._pw == _pw || (null != _pw && _pw.equals(p._pw));
        }

        if (o instanceof String)
            return o.equals(_pw);

        return false;
    }

