        // Verify the signature
        DSAPublicKeyParameters pubKey = new DSAPublicKeyParameters(y, dsaParams);

        dsa.init(false, pubKey);

        dsa.update(M, 0, M.length);

        if (!dsa.verifySignature(encSig))
        {
            fail("signature fails");
        }
    }

